import React, { useState } from "react";
import Modal from 'react-modal';

const modalables = {
    '1': <img src="portrait.jpeg" alt="Portrait" style={{ width:'39vw' }} />,
    '2': <p>beans</p>  
}

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
  };

  Modal.setAppElement('body')

const ModalView = ({ isVisible, content }) => {
    const [isOpen,setOpen] = useState(isVisible);

  return (
    isVisible ? 
    <Modal
        isOpen={isOpen}
        onRequestClose={() => setOpen(false)}
        style={customStyles}
        contentLabel="Portrait"
    >
        {content}
    </Modal>
    : 
    <div onClick={() => setOpen(true)}>
        {content}
    </div>
  );
}
export default ModalView
